import React, { FC } from "react";
import { Link, RouteComponentProps } from "react-router-dom";
import { useForm } from "react-hook-form";
import { ToastContainer, toast, Flip } from "react-toastify";
import "react-toastify/dist/ReactToastify.min.css";

type SomeComponentProps = RouteComponentProps;
const SignUp: FC<SomeComponentProps> = ({ history }): JSX.Element => {
  const { register, handleSubmit, watch, reset, formState: { errors } } = useForm();

  const signUp = async (data: any) => {
    const payload = {
      nombre: data.name,
      lastname: data.lastname,
      fecha: data.date,
      email: data.email,
      password: data.password,
      number: data.number,
    };
console.log(payload);
    try {
      const response = await fetch('http://localhost:8088/api/usuarios/registrar', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(payload),
      });

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      const responseData = await response.json();
      console.debug("Respuesta del servidor:", responseData);
      toast.success("Registro exitoso", {
        position: "top-right",
        autoClose: 3000,
        hideProgressBar: true,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: false,
        progress: 0,
        toastId: "my_toast",
      });

      reset();
      setTimeout(() => {
        history.push("/");
      }, 3000);

    } catch (error) {
      console.error("Error al enviar la solicitud:", error);
      toast.error("Ocurrió un error al intentar registrarse. Por favor, inténtalo nuevamente.", {
        position: "top-center",
        autoClose: 3000,
        hideProgressBar: true,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: false,
        progress: 0,
        toastId: "my_toast",
      });
    }
  };
  return (
    <>
      <div className="container">
        <div
          className="row d-flex justify-content-center align-items-center"
          style={{ height: "100vh" }}
        >
          <div className="card mb-3 mt-3 rounded" style={{ maxWidth: "500px" }}>
            <div className="col-md-12">
              <div className="card-body">
                <h3 className="card-title text-center text-secondary mt-3 mb-3">
                  Registro de personal
                </h3>
                <form
                  className="row"
                  autoComplete="off"
                  onSubmit={handleSubmit(signUp)}
                >
                  <div className="col-md-6">
                    <div className="">
                      <label className="form-label">Nombre</label>
                      <input
                        type="text"
                        className="form-control form-control-sm"
                        id="exampleFormControlInput1"
                        {...register("name", {
                          required: "El nombre es requerido!",
                        })}
                      />
                      {errors.name && (
                        <p className="text-danger" style={{ fontSize: 14 }}>
                          {errors.name.message}
                        </p>
                      )}
                    </div>
                  </div>
                  
                  <div className="col-md-6">
                    <div className="">
                      <label className="form-label">Apellidos</label>
                      <input
                        type="text"
                        className="form-control form-control-sm"
                        id="exampleFormControlInput2"
                        {...register("lastname", {
                          required: "Tu apellido es requerido!", 
                        })}
                      />
                      {errors.lastname && (
                        <p className="text-danger" style={{ fontSize: 14 }}>
                          {errors.lastname.message}
                        </p>
                      )}
                    </div>
                  </div>

                  <div className="">
                    <label className="form-label">Email</label>
                    <input
                      type="email"
                      className="form-control form-control-sm"
                      id="exampleFormControlInput3"
                      {...register("email", { required: "Es requerido!" })}
                    />
                    {errors.email && (
                      <p className="text-danger" style={{ fontSize: 14 }}>
                        {errors.email.message}
                      </p>
                    )}
                  </div>
                  <div className="col-md-6">
                    <div className="">
                      <label className="form-label">Fecha de nacimiento</label>
                      <input
                        type="date"
                        className="form-control form-control-sm"
                        id="exampleFormControlInput1"
                        {...register("date", {
                          required: "La fecha es requerida!",
                        })}
                      />
                      {errors.date && (
                        <p className="text-danger" style={{ fontSize: 14 }}>
                          {errors.date.message}
                        </p>
                      )}
                    </div>
                  </div>
                  <div className="col-md-6">
                    <div className="">
                      <label className="form-label">Numero Telefonico</label>
                      <input
                        type="text"
                        className="form-control form-control-sm"
                        id="exampleFormControlInput2" 
                        {...register("number", {
                          required: "Se requiere el numero telefonico",
                          pattern: {
                            value:  /^\(?([0-9]{3})\)?[-. ]?([0-9]{3})[-. ]?([0-9]{4})$/,
                            message: "Formato de número telefónico inválido. Debe ser un número de 10 dígitos."
                          }
                        })}
                      />
                      {errors.number && (
                        <p className="text-danger" style={{ fontSize: 14 }}>
                          {errors.number.message}
                        </p>
                      )}
                    </div>
                  </div>

                  <div className="">
                    <label className="form-label">Crea una Contraseña</label>
                    <input
                      type="password"
                      className="form-control form-control-sm"
                      id="exampleFormControlInput5"
                      {...register("password", {
                        required: "La contraseña es requerida",
                      })}
                    />
                    {errors.password && (
                      <p className="text-danger" style={{ fontSize: 14 }}>
                        {errors.password.message}
                      </p>
                    )}
                  </div>
                  <div className="">
                    <label className="form-label">Confirmar contraseña</label>
                    <input
                      type="password"
                      className="form-control form-control-sm"
                      id="exampleFormControlInput6"
                      {...register("cpassword", {
                        required: "cofirme su contrasña",

                        validate: (value) =>
                          value === watch("password") ||
                          "La contraseña no coincide echale un ojo",
                      })}
                    />
                    {errors.cpassword && (
                      <p className="text-danger" style={{ fontSize: 14 }}>
                        {errors.cpassword.message}
                      </p>
                    )}
                  </div>
                  <div className="text-center mt-4 ">
                    <button
                      className="btn btn-outline-primary text-center shadow-none mb-3"
                      type="submit"
                    >
                      Submit
                    </button>
                    <p className="card-text">
                      Regresar al{" "}
                      <Link style={{ textDecoration: "none" }} to={"/login"}>
                        Log In
                      </Link>
                    </p>
                  </div>
                </form>
              </div>
            </div>
          </div>
        </div>
      </div>
      <ToastContainer
        position="top-right"
        autoClose={5000}
        hideProgressBar
        closeOnClick
        rtl={false}
        pauseOnFocusLoss={false}
        draggable={false}
        pauseOnHover
        limit={1}
        transition={Flip}
      />
    </>
  );
};

export default SignUp;
