// userDeleteContext.tsx
import React, { createContext, useState, FC } from 'react';

interface UserDeleteContextType {
  eliminarUsuarios: (selectedRows: any[]) => Promise<void>;
}

export const userDeleteContext = createContext<UserDeleteContextType | null>(null);

export const UserDeleteProvider: FC = ({ children }) => {
  const eliminarUsuarios = async (selectedRows: any[]) => {
    try {
      const response = await fetch('/api/eliminar-usuarios', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ usuarios: selectedRows }),
      });

      if (!response.ok) {
        throw new Error('Error al eliminar usuarios');
      }

      // Manejar la respuesta aquí si es necesario
    } catch (error) {
      console.error('Error al eliminar usuarios:', error);
    }
  };

  return (
    <userDeleteContext.Provider value={{ eliminarUsuarios }}>
      {children}
    </userDeleteContext.Provider>
  );
};
