import React, {  useState, useContext } from "react";
import { createContext,  } from "vm";

interface RegisterContextType {
    RegisterComponente: boolean;
    setRegisterCompnente: React.Dispatch<React.SetStateAction<boolean>>;
}

export const RegisterContext = createContext<RegisterContextType | undefined>(undefined);

export const RegisterProvider: React.FC = ({children}) => {

    const [registerComponente, setRegisterComponente] = useState(false);


    return (
        <RegisterContext.Provider value={{ registerComponente, setRegisterComponente }}>
          {children}
        </RegisterContext.Provider>
      );
    };
    export const useRegisterContext = () => {
        const context = useContext(RegisterContext);
        if (!context) {
          throw new Error("useRegisterContext debe ser utilizado dentro de un proveedor RegisterProvider");
        }
        return context;
      };
