import React, { createContext, useContext, useState, ReactNode } from "react";

type ModalContextType = {
  showUsuarios:  boolean;
  openUser: () => void;
  closeUser: () => void;
};

const ListUserContext = createContext<ModalContextType | undefined>(undefined);

export const useListUserContext = () => {
  const context = useContext(ListUserContext);
  if (!context) {
    throw new Error("useModalContext must be used within a ModalProvider");
  }
  return context;
};

export const ModalProvider: React.FC<{ children: ReactNode }> = ({ children }) => {
  const [showUsuarios, setShowUsuarios] = useState(false);

  const openUser = () => setShowUsuarios(true);
  const closeUser = () => setShowUsuarios(false);

  return (
    <ListUserContext.Provider value={{ showUsuarios, openUser, closeUser }}>
      {children}
    </ListUserContext.Provider>
  );
};
