import React, { FC, useContext } from "react";
import DataTable from "react-data-table-component";
import "../vitasdash/VitasDashcss/dash1.css";
import { UsuariosContext } from "../../context/dashtabla/UsersTableContext";
import ModalEditarUsuario from "../../Components/vitasdash/Modal/ModalEditarUsuario";

const columns = [
  {
    name: "Nombre",
    selector: (row: any) => row.nombre,
    sortable: true,
  },
  {
    name: "Apellido",
    selector: (row: any) => row.apellido,
    sortable: true,
  },
  {
    name: "Email-@",
    selector: (row: any) => row.email,
    sortable: true,
  },
  {
    name: "Fecha de nacimiento",
    selector: (row: any) => row.fechaNacimiento,
  },
  {
    name: "Telefono",
    selector: (row: any) => row.telefono,
  },
  {
    name: "Acciones",
    selector: (row: any) => row.acciones,
  },
];

const Dash1: FC = () => {
  const context = useContext(UsuariosContext);

  if (!context) {
    return <div>Cargando...</div>;
  }

  const { usuarios, modalAbierto, usuarioSeleccionado, cerrarModal } = context;

  console.log('Modal abierto:', modalAbierto);
  console.log('Usuario seleccionado:', usuarioSeleccionado);

  return (
    <div className="table-wrapper">
      <DataTable
        title="Usuarios registrados"
        columns={columns}
        data={usuarios}
        pagination
        paginationPerPage={5}
        selectableRows
        fixedHeader
        contextActions={<button onClick={() => alert('Eliminar')} style={{backgroundColor: "red", borderRadius: "10px", color: "white", borderColor:"white", height:"45px", width:"50px"}}>
          <img src="/iconos/Delete.png" style={{height:"45px"}}></img>
          </button >}
      />
      {modalAbierto && usuarioSeleccionado && (
        <ModalEditarUsuario
          usuario={usuarioSeleccionado}
          cerrarModal={cerrarModal}
        />
      )}
    </div>
  );
};

export default Dash1;
