            const response = await fetch('http://localhost:8088/api/auth/login', {
              method: 'POST',
              headers: {
                'Content-Type': 'application/json'
              },
              body: JSON.stringify(data)
            });
      
            if (!response.ok) {
              const errorData = await response.json();
              if (response.status === 400) {
                toast.error(errorData.message || "Email no encontrado verifica tu correo electronico", {
                  position: "top-center",
                  autoClose: 3000,
                  hideProgressBar: true,
                  closeOnClick: true,
                  pauseOnHover: true,
                  draggable: false,
                  progress: 0,
                  toastId: "email_not_found",
                });
              } else if (response.status === 401) {
                toast.error(errorData.message || "Contraseña incorrecta verifica la contraseña", {
                  position: "top-center",
                  autoClose: 3000,
                  hideProgressBar: true,
                  closeOnClick: true,
                  pauseOnHover: true,
                  draggable: false,
                  progress: 0,
                  toastId: "invalid_password",
                });
              } else {
                throw new Error('Error en la autenticación');
              }
              throw new Error(errorData.message || 'Error en la autenticación');
            }
      
            const responseData = await response.json();
            toast.success("Login exitoso", {
              position: "top-right",
              autoClose: 3000,
              hideProgressBar: true,
              closeOnClick: true,
              pauseOnHover: true,
              draggable: false,
              progress: 0,
              toastId: "my_toast",
            });
            localStorage.setItem("auth", responseData.token);
            setTimeout(() => {
              history.push("/");
            }, 3000);
          } catch (error: any) {
            console.error("Error al enviar solicitud:", error.message);
            toast.error(`Error: ${error.message}`, {
              position: "top-center",
              autoClose: 3000,
              hideProgressBar: true,
              closeOnClick: true,
              pauseOnHover: true,
              draggable: false,
              progress: 0,
              toastId: "my_toast",
            });
          }
        };
      
