// userDeleteContext.tsx

import React, { createContext, useState, ReactNode } from 'react';

interface UserDeleteContextProps {
  eliminarUsuarios: (idsUsuarios: number[]) => Promise<void>;
}

export const UserDeleteContext = createContext<UserDeleteContextProps | undefined>(undefined);

export const UserDeleteProvider: React.FC<{ children: ReactNode }> = ({ children }) => {
  const eliminarUsuarios = async (idsUsuarios: number[]) => {
    try {
      // Aquí realizas la solicitud POST para eliminar los usuarios
      const response = await fetch('http://localhost:8088/api/usuarios/status/false', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ idsUsuarios }),
      });

      if (!response.ok) {
        throw new Error('Error al eliminar usuarios');
      }

      // Manejar la lógica después de eliminar, si es necesario
    } catch (error) {
      console.error('Error al eliminar usuarios:', error);
      throw error;
    }
  };

  return (
    <UserDeleteContext.Provider value={{ eliminarUsuarios }}>
      {children}
    </UserDeleteContext.Provider>
  );
};
