// userDeleteContext.tsx
import React, { createContext, FC, ReactNode } from 'react';

interface UserDeleteContextType {
  eliminarUsuarios: (idUsuarios: number[]) => Promise<void>;
}

export const UserDeleteContext = createContext<UserDeleteContextType | null>(null);

interface Props {
  children: ReactNode;
}

export const UserDeleteProvider: FC<Props> = ({ children }) => {
  const eliminarUsuarios = async (idUsuarios: number[]) => {
    try {
      const response = await fetch('http://localhost:8088/api/usuarios/status/false', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(idUsuarios),
      });

      if (!response.ok) {
        throw new Error('Error al eliminar usuarios');
      }

    } catch (error) {
      console.error('Error al eliminar usuarios:', error);
    }
  };

  return (
    <UserDeleteContext.Provider value={{ eliminarUsuarios }}>
      {children}
    </UserDeleteContext.Provider>
  );
};
