import React, { FC } from "react";
import { Usuario } from "../../../context/dashtabla/UsersTableContext";

interface ModalEditarUsuarioProps {
  usuario: Usuario | null;
  cerrarModal: () => void;
}

const ModalEditarUsuario: FC<ModalEditarUsuarioProps> = ({ usuario, cerrarModal }) => {
  if (!usuario) return null;

  return (
    <div className="modal fade show d-block" tabIndex={-1} role="dialog">
      <div className="modal-dialog" role="document">
        <div className="modal-content">
          <div className="modal-header">
            <h5 className="modal-title">Editar Usuario</h5>
            <button type="button" className="close" aria-label="Close" >
              <span aria-hidden="true">&times;</span>
            </button>
          </div>
          <div className="modal-body">
            <form>
              <div className="form-group">
                <label htmlFor="nombre">Nombre</label>
                <input type="text" className="form-control" id="nombre" value={usuario.nombre} readOnly />
              </div>
              <div className="form-group">
                <label htmlFor="apellido">Apellido</label>
                <input type="text" className="form-control" id="apellido" value={usuario.apellido} readOnly />
              </div>
              <div className="form-group">
                <label htmlFor="email">Email</label>
                <input type="email" className="form-control" id="email" value={usuario.email} readOnly />
              </div>
              <div className="form-group">
                <label htmlFor="telefono">Teléfono</label>
                <input type="tel" className="form-control" id="telefono" value={usuario.telefono} readOnly />
              </div>
              {/* Agrega aquí más campos de formulario si es necesario */}
            </form>
          </div>
          <div className="modal-footer">
            <button type="button" className="btn btn-secondary" onClick={cerrarModal}>Cerrar</button>
            <button type="button" className="btn btn-primary">Guardar Cambios</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ModalEditarUsuario;
