import React, { createContext, useState, useEffect, ReactNode } from "react";
import ModalEditarUsuario from "../../Components/vitasdash/Modal/ModalEditarUsuario";

export interface Usuario {
  nombre: string;
  apellido: string;
  email: string;
  fecha_nacimiento: Date;
  telefono: string;
  acciones: JSX.Element;
}


interface UsuariosContextProps {
  usuarios: Usuario[];
}

export const UsuariosContext = createContext<UsuariosContextProps | undefined>(undefined);

export const UsuariosProvider: React.FC<{ children: ReactNode }> = ({ children }) => {
  const [usuarios, setUsuarios] = useState<Usuario[]>([]);
  const [modalOpen, setModalOpen] = useState(false);
  const [usuarioSeleccionado, setUsuarioSeleccionado] = useState<Usuario | null>(null);

  useEffect(() => {
    // Simulación de la carga de usuarios desde una API
    fetch("http://localhost:8088/usuarios")
      .then(response => response.json())
      .then(data => {
        // Formateo de los datos de usuario y asignación de acciones
        const formattedData = data.map((usuario: any) => ({
          ...usuario,
          acciones: (
            <button
              style={{ borderStyle: 'none' }}
              type='button'
              className='btn btn-warning'
              onClick={() => {
                setUsuarioSeleccionado(usuario);
                console.log(usuario)
                setModalOpen(true);
              }}
            >
              ✏️
            </button>
          )
        }));
        setUsuarios(formattedData);
      })
      .catch(error => {
        console.error("Error al obtener los usuarios:", error);
      });
  }, []);


  const closeModal = () => {
    setModalOpen(false);
    setUsuarioSeleccionado(null);
  };

  return (
    <UsuariosContext.Provider value={{ usuarios }}>
      {children}
      {modalOpen && usuarioSeleccionado && (
        <ModalEditarUsuario
          usuario={usuarioSeleccionado}         
          closeModal={closeModal}
        />
      )}
    </UsuariosContext.Provider>
  );
};
