import React, { createContext, useContext,ReactNode } from 'react';

interface Usuario {
    id: number;
}

interface userDeleteContextoType {
    eliminarUsuarios: (usuarios: Usuario[]) => Promise<void>;
}

export const userDeleteContext = createContext<userDeleteContextoType | undefined>(undefined);

export const useUserDeleteContext = () => {
    const context = useContext(userDeleteContext);
    if (!context) {
        throw new Error('useTuContexto debe ser usado dentro de un TuContextoProvider');
    }
    return context;
};
interface UserDeleteContextProviderProps {
    children: ReactNode;
}
const userDeleteContextProvider: React.FC<UserDeleteContextProviderProps> =({ children }) => {
    const eliminarUsuarios = async (usuarios: Usuario[]) => {
        try {
            const response = await fetch('http://localhost:8088/api/usuarios/status/false', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',

                },
                body: JSON.stringify(usuarios.map(user => ({ id: user.id }))),
            });

            if (!response.ok) {
                throw new Error('Error al eliminar usuarios');
            }

            

        } catch (error) {
            console.error('Error:', error);
        
        }
    };

    return (
        <userDeleteContext.Provider value={{ eliminarUsuarios }}>
            {children}
        </userDeleteContext.Provider>
    );
};

export default userDeleteContextProvider;
